#define MAX_TAMANHO (5504/8) // = 688 frames = 55 segundos
//A mágica de todo o programa é que vai ter um bootloader arduino que vai carregar o programa inteiro a partir da serial.
//Este programa vai ser basicamente um cabeçalho com o executavel e todo o resto vai ser o video.
//O video é de 12.5 fps, 8x8 pixels de 1 bit cada um
//bit 0 mais ao "alto", bit 7 mais "abaixo", byte 0 mais "à esquerda", byte 7 mais "à direita"
//(com aspas pq dá pra girar o módulo...)
//então o utilitário vai converter o video em uma sequencia de bits, que vai embutir no .hex que vai mandar pro uC :) *magica piscando*



/*
atmega8.name=Arduino NG or older w/ ATmega8

atmega8.upload.protocol=stk500
atmega8.upload.maximum_size=7168
atmega8.upload.speed=19200

atmega8.bootloader.low_fuses=0xdf
atmega8.bootloader.high_fuses=0xca
atmega8.bootloader.path=atmega8
atmega8.bootloader.file=ATmegaBOOT.hex
atmega8.bootloader.unlock_bits=0x3F
atmega8.bootloader.lock_bits=0x0F

atmega8.build.mcu=atmega8
atmega8.build.f_cpu=8000000L
atmega8.build.core=arduino
*/
