int main(void){
	int b;
	DDRB = 0b11111111;
	DDRC = 0b11011111;
	DDRD = 0b11111110;
	
	SEQUENCIA = 0;
	NEXTB = 0b10101011;
	NEXTC = 0b00000010;
	NEXTD = 0b01010000;//todos apagados
	
	PORTB = NEXTB;
	PORTC = NEXTC;
	PORTD = NEXTD;
	
	TCCR0B = 0b00000011;//SRC = CLK/64 //TCCR0 para atmega 8
	TCNT0 = 0;
	TIMSK0 = 0b00000001; // TIMSK para atmega 8
	sei();
	
	for(b=0;b<8;b++){
		BUFFER[b] = pgm_read_byte(&(ANIMACAO[(unsigned)b+2]));
	}
	TAMANHO = pgm_read_byte(&(ANIMACAO[0])) | (pgm_read_byte(&(ANIMACAO[1]))<<8) ;
	if(TAMANHO>MAX_TAMANHO)TAMANHO = MAX_TAMANHO;
	FRAME = 1;
	
	while(1){
		_delay_ms(1);
	}
}
ISR(TIMER0_OVF_vect){
	short a, b;
	PORTB = NEXTB;
	PORTC = NEXTC;
	PORTD = NEXTD;
	TCNT0 = 256-125;//clk/64/125/8 = 125 hz taxa atualização
	NEXTB = 0b10101011;
	NEXTC = 0b00000010;
	NEXTD = 0b01010000;//todos apagados
	b = SEQUENCIA%8;
	a = BUFFER[b];
	switch(b){//PB0  PD6  PB7  PD4  PB1  PB3  PB5  PC1
		case 0:NEXTC&=0b11111101;break;
		case 1:NEXTB&=0b11011111;break;
		case 2:NEXTB&=0b11110111;break;
		case 3:NEXTB&=0b11111101;break;
		case 4:NEXTD&=0b11101111;break;
		case 5:NEXTB&=0b01111111;break;
		case 6:NEXTD&=0b10111111;break;
		case 7:NEXTB&=0b11111110;break;
	}
	//PC2  PC0  PB4  PB2  PD3  PB6  PD5  PD7
	if(a&1  )NEXTD|=0b10000000;
	if(a&2  )NEXTD|=0b00100000;
	if(a&4  )NEXTB|=0b01000000;
	if(a&8  )NEXTD|=0b00001000;
	if(a&16 )NEXTB|=0b00000100;
	if(a&32 )NEXTB|=0b00010000;
	if(a&64 )NEXTC|=0b00000001;
	if(a&128)NEXTC|=0b00000100;
	SEQUENCIA++;
	if(SEQUENCIA==80){//videos de 12.5 fps
		SEQUENCIA = 0;
		FRAME++;
		if(FRAME==TAMANHO)FRAME = 0;
		for(b=0;b<8;b++){
			BUFFER[b] = pgm_read_byte(&(ANIMACAO[(unsigned)(b+2+8*FRAME)]));
		}
	}
	//if(SEQUENCIA==0)POSICAO++;
}